mapper.select:
SELECT * FROM TstCase o
WHERE o.id = #{id}

    AND o.projectId=#{prjId}

SELECT * FROM TstCase o
WHERE o.id = #{id}

    AND o.projectId=#{prjId}

mapper.update:
UPDATE TstCase
  SET name=#{name}, updateById=#{updateById}, updateTime=NOW()
WHERE id=#{id}

UPDATE TstCase SET name=#{obj.name},priority=#{obj.priority},type=#{obj.type}, contentType=#{obj.contentType},content=#{obj.content}, estimate=#{obj.estimate},objective=#{obj.objective}, updateById=#{obj.updateById},updateTime=NOW(),
prop${item} = #{obj.prop${item}}
WHERE id=#{obj.id} AND projectId=#{projectId}

UPDATE TstCase
SET contentType=#{contentType},updateById=#{updateById},updateTime=NOW()
WHERE id=#{id} AND projectId=#{projectId}

UPDATE TstCase
SET reviewResult=#{result},updateById=#{updateById},updateTime=NOW()
WHERE id=#{id} AND projectId=#{projectId}

UPDATE TstCase
SET ${prop} = '${value}',updateById=#{updateById},updateTime=NOW()
WHERE id=#{id} AND projectId=#{projectId}

update TstCase c
set c.ordr = c.ordr+1
where c.ordr >= #{targetOrdr} and c.pId=#{targetPid} and id!=#{srcId}

update TstCase c
set c.ordr = c.ordr+1
where c.ordr > #{targetOrdr} and c.pId=#{targetPid} and id!=#{srcId}
