mapper.select:
SELECT * FROM TstCaseType o

WHERE o.id = #{id}

    AND o.orgId=#{orgId}

mapper.delete:
UPDATE TstCaseType
SET disabled=TRUE, updateTime=NOW()
WHERE id=#{id} AND orgId=#{orgId}

mapper.update:
UPDATE TstCaseType
SET isDefault=FALSE, updateTime=NOW()
WHERE orgId=#{orgId}

UPDATE TstCaseType
SET isDefault=TRUE, updateTime=NOW()
WHERE id=#{id} AND orgId=#{orgId}

UPDATE TstCaseType
SET ordr=#{ordr}, updateTime=NOW()
WHERE id=#{id} AND orgId=#{orgId}
