mapper.select:
SELECT * FROM IsuStatus o

WHERE o.id = #{id}

    AND o.orgId=#{orgId}

mapper.delete:
UPDATE IsuStatus
SET disabled=TRUE, updateTime=NOW()
WHERE id=#{id} AND orgId=#{orgId}

mapper.update:
UPDATE IsuStatus
SET isDefault=FALSE, updateTime=NOW()
WHERE orgId=#{orgId}

UPDATE IsuStatus
SET isDefault=TRUE, updateTime=NOW()
WHERE id=#{id} AND orgId=#{orgId}

UPDATE IsuStatus
SET ordr=#{ordr}, updateTime=NOW()
WHERE id=#{id} AND orgId=#{orgId}
