mapper.select:
SELECT o.*, c.label categoryName FROM IsuStatus o JOIN IsuStatusCategoryDefine c ON o.categoryId=c.id
WHERE o.orgId=#{orgId}
AND o.disabled!=TRUE AND o.deleted!=TRUE ORDER BY ordr ASC

SELECT o.*, c.label categoryName FROM IsuStatus o

JOIN IsuStatusCategoryDefine c ON o.categoryId=c.id

WHERE o.id = #{id} AND o.orgId=#{orgId}

mapper.delete:
UPDATE IsuStatus
SET disabled=TRUE, updateTime=NOW()
WHERE id=#{id} AND orgId=#{orgId}

mapper.update:
UPDATE IsuStatus
SET defaultVal=FALSE, updateTime=NOW()
WHERE orgId=#{orgId}

UPDATE IsuStatus
SET defaultVal=TRUE, updateTime=NOW()
WHERE id=#{id} AND orgId=#{orgId}

UPDATE IsuStatus
SET ordr=#{ordr}, updateTime=NOW()
WHERE id=#{id} AND orgId=#{orgId}
