mapper.select:
SELECT o.*, usr.avatar userAvatar
FROM TstMsg o LEFT JOIN TstUser usr on o.userId = usr.id
WHERE o.userId=#{userId}
AND o.title LIKE CONCAT('%','${keywords}','%' )
AND o.isRead = #{isRead}
AND o.deleted != true AND o.disabled != true ORDER BY o.createTime DESC

SELECT o.*, usr.avatar userAvatar
FROM TstMsg o LEFT JOIN TstUser usr on o.userId = usr.id
WHERE o.id=#{id}

mapper.update:
update TstMsg msg
    set msg.isRead=true
WHERE o.id=#{id}
