mapper.select:
SELECT * FROM TstOrgGroup grp WHERE grp.orgId = #{orgId}
AND grp.name LIKE CONCAT('%','${keywords}','%' )
AND grp.id NOT IN #{exceptIds}
AND grp.deleted != TRUE AND grp.disabled != TRUE ORDER BY grp.id ASC

SELECT * FROM TstOrgGroup grp
WHERE grp.orgId = #{orgId}
AND grp.name != '所有人' AND grp.deleted != TRUE AND grp.disabled != TRUE ORDER BY grp.id ASC

SELECT * FROM TstOrgGroup grp

WHERE grp.id = #{id}

    AND grp.orgId = #{orgId}

mapper.delete:
UPDATE TstOrgGroup
SET deleted=TRUE, updateTime=NOW()
WHERE id=#{id} AND orgId=#{orgId}
